import{display}from"../../tools/display";import{getType}from"../../tools/utils/typeUtils";import{assign}from"../../tools/utils/polyfills";export function sanitizeUser(newUser){var user=assign({},newUser);return["id","name","email"].forEach((function(key){key in user&&(user[key]=String(user[key]))})),user}export function checkUser(newUser){var isValid="object"===getType(newUser);return isValid||display.error("Unsupported user:",newUser),isValid}