"use strict";const exportWordlist=!1;import{id}from"@ethersproject/hash";import{defineReadOnly}from"@ethersproject/properties";import{Logger}from"@ethersproject/logger";import{version}from"./_version";export const logger=new Logger(version);export class Wordlist{constructor(locale){logger.checkAbstract(new.target,Wordlist),defineReadOnly(this,"locale",locale)}split(mnemonic){return mnemonic.toLowerCase().split(/ +/g)}join(words){return words.join(" ")}static check(wordlist){const words=[];for(let i=0;i<2048;i++){const word=wordlist.getWord(i);if(i!==wordlist.getWordIndex(word))return"0x";words.push(word)}return id(words.join("\n")+"\n")}static register(lang,name){name||(name=lang.locale)}}