import*as React from"react";const MISSING_CONTEXT_ERROR="Couldn't find a schedule context.";export const ScheduleUpdateContext=React.createContext({scheduleUpdate(){throw new Error(MISSING_CONTEXT_ERROR)},flushUpdates(){throw new Error(MISSING_CONTEXT_ERROR)}});export default function useScheduleUpdate(callback){const{scheduleUpdate:scheduleUpdate,flushUpdates:flushUpdates}=React.useContext(ScheduleUpdateContext);scheduleUpdate(callback),React.useEffect(flushUpdates)}