const BaseFilter=require("./base-filter"),getBlocksForRange=require("./getBlocksForRange"),{incrementHexInt:incrementHexInt}=require("./hexUtils");class TxFilter extends BaseFilter{constructor({provider:provider}){super(),this.type="tx",this.provider=provider}async update({oldBlock:oldBlock}){const toBlock=oldBlock,fromBlock=incrementHexInt(oldBlock),blocks=await getBlocksForRange({provider:this.provider,fromBlock:fromBlock,toBlock:toBlock}),blockTxHashes=[];for(const block of blocks)blockTxHashes.push(...block.transactions);this.addResults(blockTxHashes)}}module.exports=TxFilter;