module.exports=function(range,dimensions){dimensions=dimensions||2;for(var size=2*(range=range||1)+1,length=Math.pow(size,dimensions)-1,neighbors=new Array(length),i=0;i<length;i++)for(var neighbor=neighbors[i]=new Array(dimensions),index=i<length/2?i:i+1,dimension=1;dimension<=dimensions;dimension++){var value=index%Math.pow(size,dimension);neighbor[dimension-1]=value/Math.pow(size,dimension-1)-range,index-=value}return neighbors};