"use strict";const{encodeText:encodeText}=require("./util");class Base{constructor(name,code,factory,alphabet){this.name=name,this.code=code,this.codeBuf=encodeText(this.code),this.alphabet=alphabet,this.codec=factory(alphabet)}encode(buf){return this.codec.encode(buf)}decode(string){for(const char of string)if(this.alphabet&&this.alphabet.indexOf(char)<0)throw new Error(`invalid character '${char}' in '${string}'`);return this.codec.decode(string)}}module.exports=Base;