"use strict";function sampleSphere(d,rng){var rr,r,theta,h,i,v=new Array(d),d2=Math.floor(d/2)<<1,r2=0;for(i=0;i<d2;i+=2)rr=-2*Math.log(rng()),r=Math.sqrt(rr),theta=2*Math.PI*rng(),r2+=rr,v[i]=r*Math.cos(theta),v[i+1]=r*Math.sin(theta);if(d%2){var x=Math.sqrt(-2*Math.log(rng()))*Math.cos(2*Math.PI*rng());v[d-1]=x,r2+=Math.pow(x,2)}for(h=1/Math.sqrt(r2),i=0;i<d;++i)v[i]*=h;return v}module.exports=sampleSphere;