import{}from"../index.js";import{formatters}from"./formatters.js";export const fees={estimateFeesPerGas:async params=>{if(!params.request?.feeCurrency)return null;const[maxFeePerGas,maxPriorityFeePerGas]=await Promise.all([estimateFeePerGasInFeeCurrency(params.client,params.request.feeCurrency),estimateMaxPriorityFeePerGasInFeeCurrency(params.client,params.request.feeCurrency)]);return{maxFeePerGas:maxFeePerGas,maxPriorityFeePerGas:maxPriorityFeePerGas}}};async function estimateFeePerGasInFeeCurrency(client,feeCurrency){const fee=await client.request({method:"eth_gasPrice",params:[feeCurrency]});return BigInt(fee)}async function estimateMaxPriorityFeePerGasInFeeCurrency(client,feeCurrency){const feesPerGas=await client.request({method:"eth_maxPriorityFeePerGas",params:[feeCurrency]});return BigInt(feesPerGas)}