import{InvalidDefinitionTypeError}from"../../errors/abi.js";export function formatAbiItem(abiItem,{includeName:includeName=!1}={}){if("function"!==abiItem.type&&"event"!==abiItem.type&&"error"!==abiItem.type)throw new InvalidDefinitionTypeError(abiItem.type);return`${abiItem.name}(${formatAbiParams(abiItem.inputs,{includeName:includeName})})`}export function formatAbiParams(params,{includeName:includeName=!1}={}){return params?params.map((param=>formatAbiParam(param,{includeName:includeName}))).join(includeName?", ":","):""}function formatAbiParam(param,{includeName:includeName}){return param.type.startsWith("tuple")?`(${formatAbiParams(param.components,{includeName:includeName})})${param.type.slice(5)}`:param.type+(includeName&&param.name?` ${param.name}`:"")}