import{blobsToCommitments}from"./blobsToCommitments.js";import{blobsToProofs}from"./blobsToProofs.js";import{toBlobs}from"./toBlobs.js";export function toBlobSidecars(parameters){const{data:data,kzg:kzg,to:to}=parameters,blobs=parameters.blobs??toBlobs({data:data,to:to}),commitments=parameters.commitments??blobsToCommitments({blobs:blobs,kzg:kzg,to:to}),proofs=parameters.proofs??blobsToProofs({blobs:blobs,commitments:commitments,kzg:kzg,to:to}),sidecars=[];for(let i=0;i<blobs.length;i++)sidecars.push({blob:blobs[i],commitment:commitments[i],proof:proofs[i]});return sidecars}