import{ChainDoesNotSupportContract}from"../../errors/chain.js";export function getChainContractAddress({blockNumber:blockNumber,chain:chain,contract:name}){const contract=chain?.contracts?.[name];if(!contract)throw new ChainDoesNotSupportContract({chain:chain,contract:{name:name}});if(blockNumber&&contract.blockCreated&&contract.blockCreated>blockNumber)throw new ChainDoesNotSupportContract({blockNumber:blockNumber,chain:chain,contract:{name:name,blockCreated:contract.blockCreated}});return contract.address}